(17, 'Parallax', 'Others', '<style>\r\n#parallaxImg {\r\n            width: 640px;\r\n            height: 426px;\r\n            background-image: url(\"assets/browser.png\");\r\n            background-size: 125%;\r\n            background-repeat: no-repeat;\r\n}\r\n</style>\r\n<h2>Example Parallax</h2>\r\n        <i>Move your mouse over the image to see the effect!</i><br>\r\n        <blockquote>\r\n            <h4>\r\n                <svg xmlns=\"http://www.w3.org/2000/svg\" width=\"32\" height=\"32\" fill=\"currentColor\" class=\"text-error\"\r\n                    viewBox=\"0 0 16 16\" style=\"margin-bottom: -6px;\">\r\n                    <path\r\n                        d=\"M11.46.146A.5.5 0 0 0 11.107 0H4.893a.5.5 0 0 0-.353.146L.146 4.54A.5.5 0 0 0 0 4.893v6.214a.5.5 0 0 0 .146.353l4.394 4.394a.5.5 0 0 0 .353.146h6.214a.5.5 0 0 0 .353-.146l4.394-4.394a.5.5 0 0 0 .146-.353V4.893a.5.5 0 0 0-.146-.353L11.46.146zM8 4c.535 0 .954.462.9.995l-.35 3.507a.552.552 0 0 1-1.1 0L7.1 4.995A.905.905 0 0 1 8 4zm.002 6a1 1 0 1 1 0 2 1 1 0 0 1 0-2z\" />\r\n                </svg> Important Notice: You will need the parallax JS Plugin to use this feature.\r\n            </h4>\r\n        </blockquote>\r\n        <div class=\"bgWhite\">\r\n            <div id=\"parallaxImg\"></div>\r\n        </div>\r\n        <br><br>\r\n\r\n        <pre><code class=\"language-html\">\r\n&lt;div id=\"parallaxImg\"&gt;&lt;/div&gt;\r\n&lt;style&gt;\r\n    #parallaxImg {\r\n        width: 640px;\r\n        height: 426px;\r\n        background-image: url(./aurora.jpg);\r\n        background-size: 140%;\r\n    }\r\n&lt;/style&gt;\r\n&lt;script&gt;\r\n    parallax(\"#parallaxImg\", 0.02);\r\n&lt;/script&gt;</code></pre>\r\n\r\n\r\n        <script>\r\n            // @license magnet:?xt=urn:btih:d3d9a9a6595521f9666a5e94cc830dab83b65699&dn=expat.txt Expat\r\n            // modal pzplUI Plugin\r\n            \"use strict\";\r\n\r\n\r\n            let elementer;\r\n            let scaler;\r\n\r\n            function parallax(element, scale) {\r\n                document.addEventListener(\"mousemove\", parallaxFramework);\r\n                elementer = document.querySelector(element);\r\n                scaler = scale;\r\n                function parallaxFramework(e) {\r\n                    let windowWidth = window.innerWidth / 2;\r\n                    let windowHeight = window.innerHeight / 2;\r\n                    let _mouseX = e.clientX;\r\n                    let _mouseY = e.clientY;\r\n                    let _scale2 = scaler * 2;\r\n                    let _depth1 = `${(_mouseX - windowWidth) * scaler}% ${(_mouseY - windowHeight) * scaler}%`;\r\n                    let _depth2 = `${(_mouseX - windowWidth) * scaler}% ${(_mouseY - windowHeight) * scaler}%`;\r\n                    let _depth3 = `${(_mouseX - windowWidth) * _scale2}% ${(_mouseY - windowHeight) * _scale2}%`;\r\n                    let superWazneInfo = `${_depth3}, ${_depth2}, ${_depth1}`;\r\n                    elementer.style.backgroundPosition = superWazneInfo;\r\n                };\r\n            };\r\n\r\n\r\n            parallax(\"#parallaxImg\", 0.02);\r\n        // @license-end\r\n        </script>', NULL, '1.6'),
(18, 'Animations', 'Others', '<style>\r\n        .anim-showcase {\r\n            display: inline-block;\r\n        }\r\n    </style>\r\n<h2>Spin</h2>\r\n    <h3 class=\"anim-showcase\">Hello World</h3><br><br>\r\n    <button class=\"btn btn-normal\" onclick=\"animationFramework(\'anim-spin\', 0);\">Start animation</button>\r\n    <br><br>\r\n    <pre><code class=\"language-html\">&lt;h3 class=\"anim-spin\"&gt;Hello World&lt;/h3&gt; </code></pre>\r\n    <h2>Top</h2><br><br><br><br><br><br><br><br><br><br><br><br><br>\r\n    <h3 class=\"anim-showcase\">Hello World</h3><br><br>\r\n    <button class=\"btn btn-normal\" onclick=\"animationFramework(\'anim-top\', 1);\">Start animation</button>\r\n    <br><br><pre><code class=\"language-html\">&lt;h3 class=\"anim-top\"&gt;Hello World&lt;/h3&gt; </code></pre>\r\n    <h2>Bottom</h2>\r\n    <h3 class=\"anim-showcase\">Hello World</h3><br><br><br><br><br><br><br><br>\r\n    <button class=\"btn btn-normal\" onclick=\"animationFramework(\'anim-bottom\', 2);\">Start animation</button>\r\n    <br><br><pre><code class=\"language-html\">&lt;h3 class=\"anim-bottom\"&gt;Hello World&lt;/h3&gt; </code></pre>\r\n    <h2>Left</h2>\r\n    <h3 class=\"anim-showcase\">Hello World</h3><br><br>\r\n    <button class=\"btn btn-normal\" onclick=\"animationFramework(\'anim-left\', 3);\">Start animation</button>\r\n    <br><br><pre><code class=\"language-html\">&lt;h3 class=\"anim-left\"&gt;Hello World&lt;/h3&gt; </code></pre>\r\n    <h2>Right</h2>\r\n    <h3 class=\"anim-showcase\">Hello World</h3><br><br>\r\n    <button class=\"btn btn-normal\" onclick=\"animationFramework(\'anim-right\', 4);\">Start animation</button>\r\n    <br><br><pre><code class=\"language-html\">&lt;h3 class=\"anim-right\"&gt;Hello World&lt;/h3&gt; </code></pre>\r\n    <h2>Zoom</h2>\r\n    <h3 class=\"anim-showcase\">Hello World</h3><br><br>\r\n    <button class=\"btn btn-normal\" onclick=\"animationFramework(\'anim-zoom\', 5);\">Start animation</button>\r\n    <br><br><pre><code class=\"language-html\">&lt;h3 class=\"anim-zoom\"&gt;Hello World&lt;/h3&gt; </code></pre>\r\n    <script>\r\n        function animationFramework(className, order) {\r\n            document.querySelectorAll(\".anim-showcase\")[order].classList.add(className);\r\n            setTimeout(function () {\r\n                document.querySelectorAll(\".anim-showcase\")[order].classList.remove(className);\r\n            }, 1500);\r\n        };\r\n    </script>', NULL, '1.6');
